def alert = com.eviware.soapui.support.UISupport


def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
//log.info projectPath

def DataSheetTCNo = context.expand ( '${Properties#SlNo}')
log.info ("Data Sheet File  name :"+DataSheetTCNo)
def list = []

def response = testRunner.testCase.testSuite.project.getPropertyValue("ResponsePathCompare")
def dir = new File(response)

//def dir = new File("P:\\WORKBNCH\\GroovyTestScript\\MDMUPgrade\\SVCoreCustomerService_Account\\Target\\ResponseFile")
def TCNo              = DataSheetTCNo

def MDM1PrefixPattern = "${TCNo}"
int  MDM1Responsecount = 0;
String MDM1ResponseFilePath = null;
String MDM1ResponseFileName = null;
String MDM2ResponseFileName = null;

dir.eachFileMatch (~/.*Files_BaselineResponse_${MDM1PrefixPattern}\.xml/)
{ file ->  
    //log.info ("Specific file name :"+file.getName())
    def fileName = file.getName();
    MDM1ResponseFileName = fileName;
    def filesName = file.getName().split("\\.")[0]
    MDM1Responsecount++
    MDM1ResponseFilePath = file.getPath();

} 

log.info ("MDM1_Response(Base Line Response) file count :"+MDM1Responsecount)
log.info  ("MDM1_Response(Base Line Response) file path :"+MDM1ResponseFilePath)


def MDM2PrefixPattern = "${TCNo}"
int  MDM2Responsecount = 0;
String MDM2ResponseFilePath = null;
dir.eachFileMatch (~/.*Files_ActualResponse_${MDM1PrefixPattern}\.xml/)
{ file ->  
   //log.info ("Specific file name :"+file.getName())
    def fileName = file.getName();
    MDM2ResponseFileName = fileName;
    def filesName = file.getName().split("\\.")[0]
    MDM2Responsecount++
    MDM2ResponseFilePath = file.getPath();

} 

log.info ("MDM2_Response(Actual Response) file count :"+MDM2Responsecount)
log.info  ("MDM2_Response(Actual Response) file path :"+MDM2ResponseFilePath)

//dir.eachFileRecurse (FileType.FILES) 
//{ 
	//file ->list << file

//}

//list.each {
 // log.info( it.path)
//}

String  TempFileMDM1 = MDM1ResponseFileName.split("\\.")[0]
String MDM1Str1 = TempFileMDM1.split("\\_")[2]
String MDM1Str2 = TempFileMDM1.split("\\_")[3]
String MDM1Str3 = TempFileMDM1.split("\\_")[4]

String TCNOfromMDM1file = MDM1Str1+"_"+MDM1Str2+"_"+MDM1Str3
log.info ("MDM 1 File Name after _ split  "+TCNOfromMDM1file)


String  TempFileMDM2 = MDM2ResponseFileName.split("\\.")[0]
String MDM2Str1 = TempFileMDM2.split("\\_")[2]
String MDM2Str2 = TempFileMDM2.split("\\_")[3]
String MDM2Str3 = TempFileMDM2.split("\\_")[4]

String TCNOfromMDM2file = MDM2Str1+"_"+MDM2Str2+"_"+MDM2Str3
log.info ("MDM 2 File Name after _ split  "+TCNOfromMDM2file)



//String DataSheetTempTCName = DataSheetTCNo.split("\\_")[1]
//log.info ("TC No from Data sheet after split  "+DataSheetTempTCName)

if (DataSheetTCNo == TCNOfromMDM1file && DataSheetTCNo == TCNOfromMDM2file)

{

log.info("#########COMPARIOSN STARTED BOTH FILE EXIST IN FOLDER ###########"); 

FileReader  fr1 = new FileReader(MDM1ResponseFilePath);  
FileReader  fr2 = new FileReader(MDM2ResponseFilePath);

XMLUnit.setIgnoreWhitespace(true)
XMLUnit.setIgnoreComments(true)
XMLUnit.setIgnoreDiffBetweenTextAndCDATA(true)
XMLUnit.setNormalizeWhitespace(true)

Diff diff = new Diff(fr1, fr2);
log.info("Similar before? " + diff.similar());            
log.info("Identical before? " + diff.identical());  

def detDiff = new DetailedDiff(diff);
detDiff.overrideMatchTracker();
detDiff.overrideElementQualifier(new ElementNameQualifier()); 
detDiff.overrideElementQualifier(new ElementNameAndTextQualifier()); 
detDiff.overrideElementQualifier(new ElementNameAndAttributeQualifier()); 
log.info("Similar after? " + detDiff.similar());            
log.info("Identical after? " + detDiff.identical()); 
//detDiff.overrideElementQualifier(new RecursiveElementNameAndTextQualifier()); 

List differences = detDiff.getAllDifferences(); 
log.info "Differences  Size : "+differences.size()
for (Object object : differences) 
{                
Difference difference = (Difference)object;  
NodeDetail controlNode = difference.getControlNodeDetail();           
NodeDetail testNode = difference.getTestNodeDetail();
String controlNodeValue = controlNode.getNode();            
String testNodeValue = testNode.getNode();            
if (controlNodeValue != null) 
{                
log.info("####################");                
log.info("Control Node: " + controlNodeValue);            
}            
if (testNodeValue != null) 
{                
log.info("Test Node: " + testNodeValue);                
log.info("####################");           
}                  
log.info("***********************");                
log.info(difference);                
log.info("***********************");  

}
//(differences.size() == null || differences.size() == 0) &&  ( detDiff.similar() == true )

if ( ( detDiff.similar() == true ) || (differences.size() == null || differences.size() == 0)   )

{
	context.TestCaseStatus = "PASS"
	context.TestStatusDesc = "<font color='blue'><b>Actual MDM Response is matching with BaseLine MDM Response : <font color='green'><b>SOAP XML Response Comparison PASS</b></font><br></b></font><br>" 
	//context.TestStatusDesc = "<font color='blue'><b>Expected Response  :</b></font><br>" +ExpectedResponse+ "<font color='blue'><b>Actual Response :</b></font><br>" +ActualResponse+ "<font color='green'><b>PASS</b></font><br>"
	log.info " XML comparison  Result : No differnce in comparison"
	//log.info " XML comparison  Result : No differnce in comparison"+ExpectedResponse
}

else 

{
     context.TestCaseStatus = "FAIL"
	context.TestStatusDesc = "<font color='blue'><b>XML Comparison  Differences  :</b></font><br>"+differences+ "<font color='red'><b>Validation Failed</b></font><br>"
      //context.TestStatusDesc = "<font color='blue'><b>XML Comparison  Differences  :</b></font><br>"+controlNodeValue+ "<font color='red'><b>Validation Failed</b></font><br>"
      //log.info " XML comparison  differences "+controlNodeValue
      log.info " XML comparison  differences "+differences
}




}

else 

{
	log.info("**********BOTH FILE NOT Exists*************");
	 alert.showInfoMessage("Input File 'XML' not found!");

}

